import { Button, LinearProgress, makeStyles, Typography } from '@material-ui/core';
import axios from 'axios';
import { doc, setDoc } from 'firebase/firestore';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import CoinInfo from '../Components/CoinInfo';
import { numberWithCommas } from '../Components/CoinTable';
import { SingleCoin } from '../config/api';
import { CryptoState } from '../CryptoContext';
import { db } from '../firebase';


const CoinPage = () => {

  const { id } = useParams();
  const [coin, setCoin] = useState();

  const { currency, symbol, user, watchlist, setAlert} = CryptoState();

  const fetchCoin = async() => {
    const { data } = await axios.get(SingleCoin(id));

    setCoin(data);
  };

  const inWatchList = watchlist.includes(coin?.id);

  const addToWatchList = async () => {
    const coinRef = doc(db, "watchlist", user.uid);

    try{
      await setDoc(coinRef,
        {coins: watchlist ? [...watchlist,coin.id] : [coin.id]
    });

    setAlert({
      open: true,
      message: `${coin.name} Added to the Watchlist !`,
      type: "success",
    });
  }
    catch(error) {
      setAlert({
        open: true,
        message: error.message,
        type: "error"
      });
    }
  };

  const removeFromWatchlist = async () => {
    const coinRef = doc(db, "watchlist", user.uid);
    try {
      await setDoc(
        coinRef,
        { coins: watchlist.filter((wish) => wish !== coin?.id) },
        { merge: true }
      );
      setAlert({
        open: true,
        message: `${coin.name} Removed from the Watchlist !`,
        type: "success",
      });
    } catch (error) {
      setAlert({
        open: true,
        message: error.message,
        type: "error",
      });
    }
  };

  useEffect(() => {
    fetchCoin();
  }, []);

  const useStyles = makeStyles((theme) => ({
    container: {
      display: "flex",
      [theme.breakpoints.down("md")]: {
        flexDirection: "column",
        alignItems: "center",
      },
    },
    sidebar: {
      width: "30%",
      [theme.breakpoints.down("md")]: {
        width: "100%",
      },
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      marginTop: 25,
      borderRight: "2px solid grey",
    },
    heading: {
      fontWeight: "bold",
      marginBottom: 20,
      fontFamily: "Montserrat",
    },
    description: {
      width: "100%",
      fontFamily: "Montserrat",
      padding: 25,
      paddingBottom: 15,
      paddingTop: 0,
      textAlign: "justify",
    },
    marketData: {
      alignSelf: "start",
      padding: 25,
      paddingTop: 10,
      width: "100%",
      [theme.breakpoints.down("md")]: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      },
      [theme.breakpoints.down("xs")]: {
        alignItems: "start",
      },
    },
  }));
  
  const classes = useStyles();

  if (!coin) return <LinearProgress style={{ backgroundColor: "gold" }} />


  return ( 
    <div className={classes.container}>

      <div className={classes.sidebar}>
        <img src={coin?.image.large} alt={coin?.name} height="200" style={{ marginBottom: 20 }} />
      
        <Typography variant="h3" className={classes.heading}>
          {coin?.name}
        </Typography>

        <Typography variant="subtitle1" className={classes.description}>
          <div dangerouslySetInnerHTML={{__html: coin?.description.en.split(". ")[0]}} />
        </Typography>

        <div className={classes.marketData}>

            <span style={{ display: "flex "}}>
              <Typography variant="h5" className={classes.heading}>Rank :</Typography>
              &nbsp;&nbsp;&nbsp;
              <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
                  {coin?.market_cap_rank}
              </Typography>
            </span>

            <span style={{ display: "flex "}}>
              <Typography variant="h5" className={classes.heading}>Current Price :</Typography>
              &nbsp;&nbsp;&nbsp;
              <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
                  {symbol}{" "}{numberWithCommas(coin?.market_data.current_price[currency.toLowerCase()])}
              </Typography>
            </span>

            <span style={{ display: "flex "}}>
              <Typography variant="h5" className={classes.heading}>Market Cap :</Typography>
              &nbsp;&nbsp;&nbsp;
              <Typography variant="h5" style={{ fontFamily: "Montserrat" }}>
                  {symbol}{" "}{numberWithCommas(coin?.market_data.market_cap[currency.toLowerCase()].toString().slice(0, -6))}&nbsp;M
              </Typography>
            </span>

            {user && (<Button variant="outlined" style={{ width: "100%", height: 40, backgroundColor: "#EEBC1D"}} onClick={inWatchlist ? removeFromWatchlist : addToWatchlist}>
               {inWatchList ? "Remove from WatchList" : "Add to WatchList"} 
            </Button>)}
        </div>
      </div>

      <CoinInfo coin = {coin}/>

    </div>
  )
}

export default CoinPage
