import React from 'react'
import { AppBar, Container, makeStyles, MenuItem, Select, Toolbar, Typography } from '@material-ui/core'


const useStyles = makeStyles(() =>({
  title: {
      flex: 1,
      color: "gold",
      fontFamily: "Montserat",
      fontWeight: "bold",
      cursor: "pointer",
  },
}))
const Header = () => {

  const classes = useStyles();
  return (
    <AppBar color='transparent' position='static'>
      <Container>
        <Toolbar>
          <Typography className={classes.title}>Crypto Pedia</Typography>

          <Select variant='outlined' style={{width: 100, height: 40, marginLeft: 15}}>
            <MenuItem value={"USD"}>USD</MenuItem>
            <MenuItem value={"INR"}>INR</MenuItem>
          </Select>
        </Toolbar>
      </Container>
    </AppBar>
  )
}

export default Header
